const { payload } = msg;

if (typeof payload === 'string') {

    //node.warn(payload);

    const lines = payload.toString().trim().split(/[\n]+/);

    if (lines.length >= 2) {

        const labels = lines[lines.length - 2].trim().split(/[\s]+/);

        const values = lines[lines.length - 1].trim().split(/[\s]+/);

        const data = {};

        for (let i = 0; i < labels.length; ++i) {
            data[labels[i]] = values[i] || '0.0';
        }

        const cpu = data['%CPU'];

        const mem = data['%MEM'];

        const text = `%CPU ${cpu}, %MEM ${mem}`;

        return node.send([{ payload: { fill: 'green', shape: 'dot', text } }, { payload: cpu }, { payload: mem } ]);

    }

}

