const { payload = {} } = msg;

const { status, pid } = payload;

const timeout = context.get('timeout');

clearTimeout(timeout);

if (status === 'spawn') {

    const timeout = setTimeout(() => node.send({ payload: pid }), 100);

    context.set('timeout', timeout);

} else if (status === 'close') {

    node.send({ payload: 'kill', kill: 'SIGTERM' });

}

